import React from "react";
// node.js library that concatenates classes (strings)
import classnames from "classnames";
// javascipt plugin for creating charts
import Chart from "chart.js";
// react plugin used to create charts
import { Line, Bar } from "react-chartjs-2";
// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  NavItem,
  NavLink,
  Nav,
  Progress,
  Table,
  Container,
  Row, div,
  Col
} from "reactstrap";

// core components
import {
  chartOptions,
  parseOptions,
  chartExample1,
  chartExample2
} from "../variables/charts.jsx";
import { serverURL } from '../utils/api';
import Header from "../components/Headers/Header.jsx";

class Index extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      organisationList: []
    }
  }
  state = {
    activeNav: 1,
    chartExample1Data: "data1"
  };

  componentDidMount() {
    fetch(`${serverURL}organisation`, {
      method: 'GET',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
      },
    })
      .then((response) => response.json())
      .then((responseJson) => {
        console.log(responseJson);
        if (responseJson.status === 200) {
          this.setState({
            organisationList: responseJson.data
          })
        }
      })
      .catch((error) => {
        console.error(error);
      });
  }

  toggleNavs = (e, index) => {
    e.preventDefault();
    this.setState({
      activeNav: index,
      chartExample1Data:
        this.state.chartExample1Data === "data1" ? "data2" : "data1"
    });
    let wow = () => {
      console.log(this.state);
    };
    wow.bind(this);
    setTimeout(() => wow(), 1000);
    // this.chartReference.update();
  };
  componentWillMount() {
    if (window.Chart) {
      parseOptions(Chart, chartOptions());
    }
  }

  renderTableData() {
    return this.state.organisationList.map((organisation, index) => {
      const { id, name, location, email, isActive } = organisation //destructuring
      return (
        <tr key={id}>
          <th scope="row">{name}</th>
          <td>{email}</td>
          <td>{location}</td>
          {isActive === true ?
            <td>
              <span class="badge badge-dot mr-4">
                <i class="bg-warning"></i>  Pending
                      </span>
            </td> :
            <td>
              <span class="badge badge-dot mr-4">
                <i class="bg-success"></i>  Approved
                      </span>
            </td>}
          <td> {isActive === true ? <a href="#!" class="btn btn-sm btn-success">Accept</a> : null}</td>
        </tr>
      )
    })
  }
  render() {
    return (
      <>

        <Header />
        {/* Page content */}
        <Container className="mt--7" fluid>
          <Row className="mt-5">
            <Col className="mb-5 mb-xl-0" xl="12">
              <Card className="shadow">
                <Table className="align-items-center table-flush" responsive>
                  <thead className="thead-light">
                    <tr>
                      <th scope="col">Name</th>
                      <th scope="col">Email</th>
                      <th scope="col">Location</th>
                      <th scope="col">Status</th>
                      <th scope="col">Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {this.renderTableData()}
                  </tbody>
                </Table>
              </Card>
            </Col>
          </Row>
          <div class="card-body px-lg-5 py-lg-5">
            <div class="text-center text-muted mb-4">
              <small>Please enter your wallet credentials</small>
            </div>
            <form role="form">
            <div class="form-group mb-3">
                <div class="input-group input-group-alternative">
                    <div class="input-group-prepend">
                        <span class="input-group-text"><i class="ni ni-email-83"></i></span>
                    </div>
                    <input class="form-control" placeholder="Email" type="email"/>
                </div>
            </div>
            <div class="form-group">
                <div class="input-group input-group-alternative">
                    <div class="input-group-prepend">
                        <span class="input-group-text"><i class="ni ni-lock-circle-open"></i></span>
                    </div>
                    <input class="form-control" placeholder="Password" type="password"/>
                </div>
            </div>
            <div class="text-center">
                <button type="button" class="btn btn-primary my-4">Sign in</button>
            </div>
            </form>
          </div>
        </Container>
      </>
    );
  }
}

export default Index;
